<script>
  document.addEventListener("DOMContentLoaded", function() {
    let rows = document.querySelectorAll('.item-row');
    let subtotal = 0;
    rows.forEach((row, index) => {
      subtotal += parseFloat(row.querySelector('.price').textContent);

      // Check for page break condition
      if ((index + 1) % 20 === 0 || index === rows.length - 1) {  // Assume 20 rows per page
        let subtotalRow = document.createElement('tr');
        subtotalRow.classList.add('subtotal-row');
        subtotalRow.innerHTML = `<td colspan="3">Subtotal</td><td>${subtotal.toFixed(2)}</td>`;
        row.parentNode.insertBefore(subtotalRow, row.nextSibling);
        subtotal = 0;
